import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { SaveOutlined, SaveFilled, MenuOutlined, AreaChartOutlined, GlobalOutlined, DollarCircleOutlined, UserOutlined, BookOutlined, LogoutOutlined, RocketOutlined } from '@ant-design/icons';
import axios from 'axios';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faBookmark} from '@fortawesome/free-solid-svg-icons';
import Header from '../../components/Header';
import Footer from '../../components/Footer';

const NewsComponent = () => {
  const [newsItems, setNewsItems] = useState([]);
  const [error, setError] = useState('');
  const [menuOpen, setMenuOpen] = useState(false);
  const [userName, setUserName] = useState('');
  const [savedArticles, setSavedArticles] = useState([]);
 

  const toggleMenu = () => {
      setMenuOpen(!menuOpen);
  };

  const handleSave = (item) => {
    // Bookmarking logic
    const itemExists = savedArticles.find((saved) => saved.id === item.id);
    if (itemExists) {
      // Remove from saved articles if already saved
      setSavedArticles(savedArticles.filter((saved) => saved.id !== item.id)); 
    } else {
      // Add to saved articles
      const newSavedArticle = {
        ...item, 
        savedBy: userName
      };
      setSavedArticles([...savedArticles, newSavedArticle]);
    }

    // Update bookmarked state in newsItems (for icon change)
    setNewsItems(newsItems.map((newsItem) => {
      // Update ONLY the matching item
      if (newsItem.id === item.id) { 
        return { ...newsItem, bookmarked: !newsItem.bookmarked }; 
      } else {
        return newsItem; // Keep other items unchanged
      }
    }));
  };

  useEffect(() => {
    const fetchNews = async () => {
      const options = {
        method: 'GET',
        url: 'https://bloomberg-market-and-financial-news.p.rapidapi.com/stories/list',
        params: { template: 'CURRENCY', id: 'usdjpy' },
        headers: {
          'X-RapidAPI-Key': 'bd76d4e673msh4b9484eba9d350ep1d327bjsne55e1cf00ce3',
          'X-RapidAPI-Host': 'bloomberg-market-and-financial-news.p.rapidapi.com'
        }
      };

      try {
        const response = await axios.request(options);
        setNewsItems(response.data.stories);
      } catch (err) {
        setError('Failed to fetch news');
        console.error(err);
      }
    };

    fetchNews();
  }, []);

  useEffect(() => {
    const storedUserInfo = localStorage.getItem('userInfo');
    if (storedUserInfo) {
      const userInfo = JSON.parse(storedUserInfo);
      setUserName(userInfo.name || 'Guest');
    }
  }, []);

  if (error) {
    return <div className="text-red-500 text-center">{error}</div>;
  }

  const handleLogout = () => {
    localStorage.removeItem('userInfo'); 
    window.location.href = '/login'; 
  };

  return (
    <>
    <div className="bg-indigo-50">
      <div class="justify-center pt-1 bg-teal-950">
            <h2 class="text-amber-500 text-xl font-bold place-self-center mt-2">Global Agri-Export News</h2>
      </div>
      <div className={`fixed inset-y-0 left-0 z-50 w-64 bg-indigo-50 transition duration-300 ease-in-out transform ${menuOpen ? 'translate-x-0' : '-translate-x-full'}`}>
          <div className="flex items-center justify-between p-4">
          <a href="/home">
            <img src="/logo.png" alt="Your Company Logo" className="w-15 h-8 hover:-translate-y--1 hover:scale-110" />
          </a>
            <button className=" text-neutral-800 focus:outline-none" onClick={toggleMenu}>
              <MenuOutlined />
            </button>
          </div>
          <nav className="p-4">
            <ul className="space-y-4">
            <li><Link to="/identify" className=" text-neutral-800 bg-rounded-lg px-3 py-2 flex indent-4 hover:font-medium items-center   font-sans"><AreaChartOutlined /> Market Predictions</Link></li>
            <li><Link to="/worldwide-news" className=" text-white bg-teal-950 px-6 py-2 flex items-center indent-4 hover:font-medium px-3 py-2 font-medium"><GlobalOutlined /> Worldwide News</Link></li>
            
            <li><Link to="/find-buyers" className=" text-neutral-800 flex items-center indent-4 px-3 py-2 hover:font-medium" ><UserOutlined /> Find Buyers</Link></li>
            <li><Link to="/lessions" className=" text-neutral-800 flex items-center indent-4 px-3 p3-2 hover:font-medium"><BookOutlined /> Find Lessons</Link></li>
            <li><Link to="/escrow" className=" text-neutral-800 flex items-center indent-4 px-3 py-2 hover:font-medium"><DollarCircleOutlined /> Go to Escrow</Link></li>
            <li><Link to="/home" className="border-opacity-30 animate-pulse border-amber-500 rounded-lg border-2 border-dashed text-amber-500 flex items-center indent-4 px-3 py-2 hover:font-medium " ><RocketOutlined /> C2W Premium</Link></li>
            </ul>
          </nav>
        </div>

        <header className="bg-teal-950 shadow">
            <div className="flex justify-between items-center px-4 py-2 border-b">
              <button className="animate-pulse text-white focus:outline-none hover:text-amber-500" onClick={toggleMenu}>
                <MenuOutlined />
              </button>
              <div className="flex items-center">
                <div className="text-sm font-medium text-white mr-4">Hello!{userName}</div>
                <button onClick={handleLogout} className="flex items-center text-white hover:text-amber-500">
                  <LogoutOutlined className="mr-2" /> Logout
                </button>
              </div>
            </div>
          </header>


      <div className="container mx-auto px-4 py-8 bg-indigo-50">
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8 pl-16 pr-16 ">
          {newsItems.map((item, index) => (
            <div key={index} className="max-w-sm rounded overflow-hidden shadow-lg bg-white mb-2 ml-2 mr-2 hover:-translate-y--1 hover:scale-105">
              
              <div className="absolute top-5 right-5 mt-3 mr-3 "> 
               <a  target="_blank" rel="noopener noreferrer" className="text-amber-500">
               {item.bookmarked ? (
                <SaveFilled style={{ fontSize: '28px' }} onClick={() => handleSave(item)} /> 
              ) : (
                <SaveOutlined style={{ fontSize: '28px' }} onClick={() => handleSave(item)} /> 
              )}
               </a>
              </div>
              {item.thumbnailImage && (
                <img className="w-full h-48 object-cover" src={item.thumbnailImage} alt="News thumbnail" />
                
              )}
              <a href={item.longURL} target="_blank" rel="noopener noreferrer" className="text-neutral-800">
              <div className="px-6 py-4 ">
                <div className="text-teal-950 font-light text-lg mb-2 text-left leading-5.5">{item.title}</div>
                <p className="text-base">
                  <a href={item.longURL} target="_blank" rel="noopener noreferrer" className="font-semibold text-amber-500 hover:text-teal-950">
                    Read more
                  </a>
                </p>
              </div>
              </a>
              
            </div>
          ))}
        </div>
      </div>
      <div className="bg-indigo-50 pt-5 pb-5">
      <footer class="bg-indigo-50 rounded-lg shadow bg-teal-950 m-4">
        <div class="w-full max-w-screen-xl mx-auto p-4 md:py-8 rounded-lg bg-teal-950">
          <div class="sm:flex sm:items-center sm:justify-between">
              <a href="/home" class="flex items-center mb-4 sm:mb-0 space-x-3 rtl:space-x-reverse bg-white ml-5 py-3 px-3 pr-2 rounded-lg bg-opacity-85">
                <img src="/logo.png" class="h-8" alt="Flowbite Logo" />
                <span class="self-center text-2xl font-semibold whitespace-nowrap dark:text-white"></span>
              </a> 
            <ul class="flex flex-wrap items-center mb-6 text-sm font-medium text-white-500 sm:mb-0 dark:text-gray-400">
                <li>
                    <a href="#" class="hover:underline me-4 md:me-6">Predict Markets</a>
                </li>
                <li>
                    <a href="#" class="hover:underline me-4 md:me-6">Find Global News</a>
                </li>
                <li>
                    <a href="#" class="hover:underline me-4 md:me-6">Find Buyers</a>
                </li>
                <li>
                    <a href="#" class="hover:underline me-4 md:me-6">Privacy Policy</a>
                </li>
                <li>
                    <a href="#" class="hover:underline">Contact</a>
                </li>
            </ul>
          </div>
          <hr class="my-6 border-gray-200 sm:mx-auto dark:border-gray-700 lg:my-8" />
          <span class="block text-sm text-gray-500 sm:text-center dark:text-gray-400">© 2024 <a href="/home" class="hover:underline">C2W™</a>. All Rights Reserved.</span>
        </div>
        </footer>
      </div>
      </div>
    </>
  );
};

export default NewsComponent;
