import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { RocketOutlined, MenuOutlined, AreaChartOutlined, GlobalOutlined, DollarCircleOutlined, UserOutlined, BookOutlined, LogoutOutlined } from '@ant-design/icons';
import Header from '../../components/Header';
import Footer from '../../components/Footer';
import { LayoutDashboard, Home, StickyNote, Layers, Flag, Calendar, LifeBuoy, Settings } from "lucide-react";
import Sidebar, { SidebarItem } from "../../components/Sidebar"

const Dashboard = () => {
  const [menuOpen, setMenuOpen] = useState(false);
  const [userName, setUserName] = useState('');

  const [hovered1, setHovered1] = useState(false);
  const [hovered2, setHovered2] = useState(false);
  const [hovered3, setHovered3] = useState(false);
  const [hovered4, setHovered4] = useState(false);
  const [hovered5, setHovered5] = useState(false);

  const defaultSrc1 = "/predict2.png"; 
  const hoverSrc1 = "/predict1.gif";

  const defaultSrc2 = "/news2.png"; 
  const hoverSrc2 = "/news1.gif";

  const defaultSrc3 = "/escrow2.png"; 
  const hoverSrc3 = "/escrow1.gif";

  const defaultSrc4 = "/buyers2.png"; 
  const hoverSrc4 = "/buyers1.gif";

  const defaultSrc5 = "/lessons2.png"; 
  const hoverSrc5 = "/lessons1.gif";



  const toggleMenu = () => {
    setMenuOpen(!menuOpen);
  };

  useEffect(() => {
    const storedUserInfo = localStorage.getItem('userInfo');
    if (storedUserInfo) {
      const userInfo = JSON.parse(storedUserInfo);
      setUserName(userInfo.name || 'Guest');
    }
  }, []);

  const handleLogout = () => {
    localStorage.removeItem('userInfo'); 
    window.location.href = '/login'; 
  };

  return (

    
      <div className = "h-screen">
        <div className="bg-indigo-50 ">
          <header className="bg-teal-950 shadow">
            <div class="justify-center pt-1">
              <img src="/logo.png" alt="Your Company Logo" className="mt-4 mx-auto w-18 h-11 hover:-translate-y--1 hover:scale-110" />
            </div>
            


            <div className="flex justify-between  px-4 py-2 border-b">
              <button className="text-white focus:outline-none pr-2 hover:text-amber-500"  onClick={toggleMenu}>
                <MenuOutlined />
              </button>
              <div className="flex items-center">
                <div className="text-sm font-medium text-white mr-4">Hello! {userName}</div>
                <button onClick={handleLogout} className="flex items-center text-white hover:text-amber-500">
                  <LogoutOutlined className="mr-2" /> Logout
                </button>
              </div>
            </div>
          </header>
          
          <div className="flex h-screen bg-gray-100">
           

            <div className="flex flex-col flex-1">
                    

                    <main className="flex-1 bg-indigo-50">
                      <div className="flex flex-row ">
                        <div className="flex ">
                          <Sidebar>
                            <SidebarItem icon={<Home size={20} />} text="Home" alert />
                            <SidebarItem icon={<LayoutDashboard size={20} />} text="Dashboard" active />
                            <SidebarItem icon={<StickyNote size={20} />} text="Projects" alert />
                            <SidebarItem icon={<Calendar size={20} />} text="Calendar" />
                            <SidebarItem icon={<Layers size={20} />} text="Tasks" />
                            <SidebarItem icon={<Flag size={20} />} text="Reporting" />
                            <hr className="my-3" />
                            <SidebarItem icon={<Settings size={20} />} text="Settings" />
                            <SidebarItem icon={<LifeBuoy size={20} />} text="Help" />
                          </Sidebar>
                        </div>
                        <Link to="/identify" className=" bg-gray-200 hover:bg-white flex-col justify-center font-medium hover:text-amber-500 text-teal-950  py-16 rounded-3xl mx-8 my-8 h-fit"><AreaChartOutlined style={{ fontSize: '26px' }}/> 
                        <img 
                          src={hovered1 ? hoverSrc1 : defaultSrc1}
                          alt="My Image"
                          onMouseEnter={() => setHovered1(true)}
