import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { RocketOutlined, MenuOutlined, AreaChartOutlined, GlobalOutlined, DollarCircleOutlined, UserOutlined, BookOutlined, LogoutOutlined } from '@ant-design/icons';
import { QuestionMarkCircleIcon } from '@heroicons/react/24/solid';
import Header from '../../components/Header';
import Footer from '../../components/Footer';
import { LayoutDashboard, Home, StickyNote, Layers, Flag, Calendar, LifeBuoy, Settings } from "lucide-react";
import Sidebar, { SidebarItem } from "../../components/Sidebar"
import '../../styles.css';

const Dashboard = () => {
  const [menuOpen, setMenuOpen] = useState(false);
  const [userName, setUserName] = useState('');

  const [hovered1, setHovered1] = useState(false);
  const [hovered2, setHovered2] = useState(false);
  const [hovered3, setHovered3] = useState(false);
  const [hovered4, setHovered4] = useState(false);
  const [hovered5, setHovered5] = useState(false);

  const defaultSrc1 = "/predict2.png"; 
  const hoverSrc1 = "/predict1.gif";

  const defaultSrc2 = "/news2.png"; 
  const hoverSrc2 = "/news1.gif";

  const defaultSrc3 = "/escrow2.png"; 
  const hoverSrc3 = "/escrow1.gif";

  const defaultSrc4 = "/buyers2.png"; 
  const hoverSrc4 = "/buyers1.gif";

  const defaultSrc5 = "/lessons2.png"; 
  const hoverSrc5 = "/lessons1.gif";

  const [windowDimensions, setWindowDimensions] = useState({
    width: window.innerWidth,
    height: window.innerHeight,
  });

  useEffect(() => {
    const handleResize = () => {
      setWindowDimensions({
        width: window.innerWidth,
        height: window.innerHeight,
      });
    };

    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  const toggleMenu = () => {
    setMenuOpen(!menuOpen);
  };

  useEffect(() => {
    const storedUserInfo = localStorage.getItem('userInfo');
    if (storedUserInfo) {
      const userInfo = JSON.parse(storedUserInfo);
      setUserName(userInfo.name || 'Guest');
    }
  }, []);

  const [isDropdownOpen, setIsDropdownOpen] = useState(false);

  // ... your other state variables and functions ...

  const toggleDropdown = () => {
    setIsDropdownOpen(!isDropdownOpen);
  }

  const handleLogout = () => {
    localStorage.removeItem('userInfo'); 
    window.location.href = '/login'; 
  };

  return (
      <div>
        <div className="bg-indigo-50 overflow-y-auto ">
          <header className="bg-teal-950">
            <div className="flex justify-between  px-4 py-2 border-b ">
              <div class="justify-center pt-1">
                <img src="/logo.png" alt="Your Company Logo" className="w-18 h-6  mt-1" />
              </div>
              <div className="flex items-center">
              <button
                
                className="text-white focus:outline-none rounded-lg p-2 hover:text-amber-500 flex items-center" // Tailwind classes
              >
                <QuestionMarkCircleIcon className="h-6 w-6 mr-2" /> {/* Icon (optional) */}
                <span className="text-sm font-medium">Help</span>
              </button>
                
                <button 
                  id="dropdownInformationButton" 
                  data-dropdown-toggle="dropdownInformation" 
                  class="text-white font-medium text-center inline-flex " 
                  type="button"
                  aria-expanded="false" 
                  aria-haspopup="true"
                  onClick={toggleDropdown}
                >
              
                  <img src="/userprofile.png" alt="Your Company Logo" className="mt-1 size-8" />
                </button>
                <div id="dropdownInformation" className={`absolute right-0 mt-72 mr-4 ${isDropdownOpen ? '' : 'hidden'} bg-white divide-y divide-gray-100 rounded-lg shadow w-44 dark:bg-gray-700 dark:divide-gray-600`}>
                    <div class="px-4 py-3 text-sm text-gray-900 dark:text-white">
                      <div>{userName}</div>
                      <div class="font-medium truncate">wishwajayanath@gmail.com</div>
                    </div>
                    <ul class="py-2 text-sm text-gray-700 dark:text-gray-200" aria-labelledby="dropdownInformationButton">
                      <li>
                        <a href="#" class="block px-4 py-2 hover:bg-gray-100 dark:hover:bg-gray-600 dark:hover:text-white">Recommendations</a>
                      </li>
                      <li>
                        <a href="#" class="block px-4 py-2 hover:bg-gray-100 dark:hover:bg-gray-600 dark:hover:text-white">Messages</a>
                      </li>
                      <li>
                        <a href="#" class="block px-4 py-2 hover:bg-gray-100 dark:hover:bg-gray-600 dark:hover:text-white">Settings</a>
                      </li>
                    </ul>
                    <div class="py-2">
                      <a href="#" class="block px-4 py-2 hover:bg-gray-100 dark:hover:bg-gray-600 dark:hover:text-white text-white">
                        <button onClick={handleLogout} className="">
                          <LogoutOutlined className="mr-2" /> Sign out
                        </button>
                      </a>
                    </div>
                </div>
              </div>
            </div>
          </header>
          
          <div className="flex bg-gray-100">
            {/*<div className={`fixed inset-y-0 left-0 z-50 w-64 bg-indigo-50 transition duration-300 ease-in-out transform ${menuOpen ? 'translate-x-0' : '-translate-x-full'}`}>
                <div className="flex items-center justify-between p-4">
                  <a href="/home">
                    <img src="/logo.png" alt="Your Company Logo" className="w-15 h-8 hover:-translate-y--1 hover:scale-110" />
                  </a>
                  <button className=" text-neutral-800 focus:outline-none" onClick={toggleMenu}>
                    <MenuOutlined />
                  </button>
                </div>
                <nav className="p-4">
                  <ul className="space-y-4">
                  <li><Link to="/identify" className=" text-neutral-800 bg-rounded-lg px-3 py-2 flex indent-4 hover:font-medium items-center   font-sans"><AreaChartOutlined  /> Market Predictions</Link></li>
                  <li><Link to="/worldwide-news" className="text-neutral-800 flex items-center indent-4 px-3 p3-2 hover:font-medium"><GlobalOutlined /> Worldwide News</Link></li>
                  
                  <li><Link to="/find-buyers" className="text-neutral-800 flex items-center indent-4 px-3 py-2 hover:font-medium" ><UserOutlined /> Find Buyers</Link></li>
                  <li><Link to="/lessions" className="text-white bg-teal-950 px-6 py-2 flex items-center indent-4 hover:font-medium px-3 py-2 font-medium"><BookOutlined /> Find Lessons</Link></li>
                  <li><Link to="/escrow" className=" text-neutral-800 flex items-center indent-4 px-3 py-2 hover:font-medium"><DollarCircleOutlined /> Go to Escrow</Link></li>
                  <li><Link to="/home" className="border-opacity-30 animate-pulse border-amber-500 rounded-lg border-2 border-dashed text-amber-500 flex items-center indent-4 px-3 py-2 hover:font-medium " ><RocketOutlined /> C2W Premium</Link></li>
                  </ul>
                </nav>
            </div>*/}

            <div className="flex flex-col flex-1">
                    

                    <main className="flex bg-indigo-50">
                      <div className="flex flex-row ">
                        <div className="flex">
                          <Sidebar>
                            <SidebarItem icon={<Home size={20} />} text="Home" alert/>
                            <SidebarItem icon={<LayoutDashboard size={20} />} text="Dashboard" active to="/find-buyers"/>
                            <SidebarItem icon={<StickyNote size={20} />} text="Projects" alert/>
                            <SidebarItem icon={<Calendar size={20} />} text="Calendar"/>
                            <SidebarItem icon={<Layers size={20} />} text="Tasks"/>
                            <SidebarItem icon={<Flag size={20} />} text="Reporting"/>
                            <hr className="my-3"/>
                            <SidebarItem icon={<Settings size={20} />} text="Settings"/>
                            <SidebarItem icon={<LifeBuoy size={20} />} text="Help"/>
                          </Sidebar>
                        </div>
                        <div className=" flex flex-col-reverse bg-gray-200 hover:bg-white font-medium hover:text-amber-500 text-teal-950  p-4  rounded-3xl mx-4 my-4">
                          <Link to="/identify" className="  ">
                            <AreaChartOutlined style={{ fontSize: '26px' }}/>
                            <img 
                              src={hovered1 ? hoverSrc1 : defaultSrc1}
                              alt="My Image"
                              onMouseEnter={() => setHovered1(true)}
                              onMouseLeave={() => setHovered1(false)}
                              className="w-96 h-56 my-4"
                            /> 
                            <div className="border-solid border-2 border-amber-500 pl-2 pr-2 pt-2 pb-2 mb-4 rounded-lg">Predict Sales </div>  
                          </Link>
                        </div>
                        <div className=" flex flex-col-reverse bg-gray-200 hover:bg-white font-medium  text-teal-950  p-4  rounded-3xl mx-4 my-4">
                          <Link to="/worldwide-news" className=""> 
                          <GlobalOutlined style={{ fontSize: '26px' }}/>
                            <img 
                              src={hovered2 ? hoverSrc2 : defaultSrc2}
                              alt="My Image"
                              onMouseEnter={() => setHovered2(true)}
                              onMouseLeave={() => setHovered2(false)}
                              className="w-96 h-56 my-4"
                            /> 
                            
                            <div className="border-solid border-2 border-amber-500 pl-2 pr-2 pt-2 pb-2 mb-4 rounded-lg">Worldwide News</div>
                          </Link>
                        </div>
                        <div className="flex flex-col-reverse bg-gray-200 hover:bg-white font-medium hover:text-amber-500 text-teal-950  p-4  rounded-3xl mx-4 my-4">
                          <Link to="/escrow" className=""> 
                          <DollarCircleOutlined style={{ fontSize: '26px' }}/>
                            <img 
                              src={hovered3 ? hoverSrc3 : defaultSrc3}
                              alt="My Image"
                              onMouseEnter={() => setHovered3(true)}
                              onMouseLeave={() => setHovered3(false)}
                              className="w-96 h-56 my-4"
                            />
                            
                            <div className="border-solid border-2 border-amber-500 pl-2 pr-2 pt-2 pb-2 mb-4 rounded-lg">Go to Escrow</div>
                          </Link>
                        </div>
                        <div className="flex flex-col-reverse bg-gray-200 hover:bg-white font-medium hover:text-amber-500 text-teal-950  p-4  rounded-3xl mx-4 my-4">
                          <Link to="/find-buyers" className=""> 
                            <UserOutlined style={{ fontSize: '26px' }}/> 
                            <img 
                              src={hovered4 ? hoverSrc4 : defaultSrc4}
                              alt="My Image"
                              onMouseEnter={() => setHovered4(true)}
                              onMouseLeave={() => setHovered4(false)}
                              className="w-96 h-56 my-4"
                            />
                            <div className="border-solid border-2 border-amber-500 pl-2 pr-2 pt-2 pb-2 mb-4 rounded-lg">Find Buyers</div>
                          </Link>
                          
                        </div>
                        <div className="flex flex-col-reverse bg-gray-200 hover:bg-white font-medium hover:text-amber-500 text-teal-950  p-4  rounded-3xl mx-4 my-4">
                          <Link to="/lessions" className="">
                            <BookOutlined style={{ fontSize: '26px' }}/> 
                            <img 
                              src={hovered5 ? hoverSrc5 : defaultSrc5}
                              alt="My Image"
                              onMouseEnter={() => setHovered5(true)}
                              onMouseLeave={() => setHovered5(false)}
                              className="w-96 h-44 my-10"
                            />
                            <div className="border-solid border-2 border-amber-500 pl-2 pr-2 pt-2 pb-2 mb-4 rounded-lg">Find Lessons</div>
                          </Link>
                          
                        </div>
                      </div>
                    </main>
                    <footer class="bg-teal-950 rounded-lg shadow bg-teal-950 p-4 md:py-8 bg-teal-950 rounded-lg">
                          <div class="sm:flex sm:items-center sm:justify-between bg-teal-950">
                              <a href="/home" class="flex items-center mb-4 sm:mb-0 space-x-3 rtl:space-x-reverse  ml-5 py-3 px-3 pr-2 rounded-lg bg-opacity-85">
                                <img src="/logo.png" class="h-8" alt="c2w Logo" />
                              </a> 
                              <ul class="flex flex-wrap items-center text-sm font-medium text-white-500  dark:text-gray-400">
                                <li>
                                  <a href="#" class="hover:underline me-4 md:me-6">Predict Markets</a>
                                </li>
                                <li>
                                  <a href="#" class="hover:underline me-4 md:me-6">Find Global News</a>
                                </li>
                                <li>
                                  <a href="#" class="hover:underline me-4 md:me-6">Find Buyers</a>
                                </li>
                                <li>
                                  <a href="#" class="hover:underline me-4 md:me-6">Privacy Policy</a>
                                </li>
                                <li>
                                  <a href="#" class="hover:underline">Contact</a>
                                </li>
                              </ul>
                          </div>
                          <hr class="my-6 border-gray-200 sm:mx-auto dark:border-gray-700 lg:my-8" />
                          <span class="block text-sm text-gray-500 sm:text-center dark:text-gray-400"> 2024 <a href="/home" class="hover:underline">C2W</a>. All Rights Reserved.</span>
                    </footer>
                </div>
          </div>
      </div>
    </div>

  );
};

export default Dashboard;
