import { Link } from 'react-router-dom';
import { MenuOutlined, AreaChartOutlined, GlobalOutlined, DollarCircleOutlined, UserOutlined, BookOutlined, LogoutOutlined, RocketOutlined } from '@ant-design/icons';
import axios from 'axios';
import { Line } from 'react-chartjs-2';
import { Chart, CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend } from 'chart.js';
import Header from '../../components/Header';
import Footer from '../../components/Footer';

// Register components required for Line chart
Chart.register(CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend);

const StockData = () => {
  const [country, setCountry] = useState('');
  const [productID, setProductID] = useState('');
  const [chartData, setChartData] = useState({});
  const [menuOpen, setMenuOpen] = useState(false);
  const [userName, setUserName] = useState('');



  const toggleMenu = () => {
    setMenuOpen(!menuOpen);
  };

  useEffect(() => {
    if (country && productID) {
      const fetchData = async () => {
        try {
          const response = await axios.get(`http://localhost:5001/fetch_chart_data`, {
            params: { country: country, product_id: productID }
          });
          const transformedData = transformChartData(response.data);
          setChartData(transformedData);
        } catch (error) {
          console.error('Error fetching chart data:', error);
        }
      };
      fetchData();
    }
  }, [country, productID]);

  useEffect(() => {
    const storedUserInfo = localStorage.getItem('userInfo');
    if (storedUserInfo) {
      const userInfo = JSON.parse(storedUserInfo);
      setUserName(userInfo.name || 'Guest');
    }
  }, []);

  const handleLogout = () => {
    localStorage.removeItem('userInfo'); 
    window.location.href = '/login'; 
  };

  // Function to transform chart data
  function transformChartData(data) {
    const labels = data.map(item => item.month);
    const salesData = data.map(item => item.sales);
    const predictionsData = data.map(item => item.prediction);
    const nextMonths = getNextMonths(labels[labels.length - 1], 5); // Get labels for the next 5 months

    // Concatenate the existing labels with the labels for the next months
    const allLabels = [...labels, ...nextMonths];

    // Fill in the sales and prediction data for the next months with null values
    const allSalesData = [...salesData, ...Array(5).fill(null)];
    const allPredictionsData = [...Array(labels.length).fill(null), ...predictionsData];

    return {
      labels: allLabels,
      datasets: [
        {
          label: `Sales for ${data[0]?.product_name || 'Unknown Product'}`,
          backgroundColor: 'rgba(40,70,75,0.6)',
          borderColor: 'rgba(40,70,75,1)',
          borderWidth: 1,
          hoverBackgroundColor: 'rgba(75,192,192,0.6)',
          hoverBorderColor: 'rgba(75,192,192,1)',
          data: allSalesData
        },
        {
          label: 'Predictions',
          backgroundColor: 'rgba(255,219,88, 0.6)',  // Red color
          borderColor: 'rgba(255,219,88, 1)',
          borderWidth: 1,
          hoverBackgroundColor: 'rgba(255, 99, 132, 0.4)',
          hoverBorderColor: 'rgba(255, 99, 132, 1)',
          data: allPredictionsData
        }
      ]
    };
  }

  // Function to get labels for the next n months based on the last month in the data
  function getNextMonths(lastMonth, n) {
    const months = ['January', 'February', 'March', 'April', 'May', 'June', 'July', 'August', 'September', 'October', 'November', 'December'];
    const lastMonthIndex = months.indexOf(lastMonth);
    const nextMonths = [];
    for (let i = 1; i <= n; i++) {
      const nextMonthIndex = (lastMonthIndex + i) % 12;
      nextMonths.push(months[nextMonthIndex]);
    }
    return nextMonths;
  }

  const handleCountryChange = (e) => setCountry(e.target.value);
  const handleProductChange = (e) => setProductID(e.target.value);

  const options = {
    responsive: true,
    plugins: {
      legend: {
        position: 'top'
      }
    },
    scales: {
      x: {
        type: 'category',
        title: {
          display: true,
          text: 'Month'
        }
      }
    }
  };

  return (
    <div className="flex flex-col min-h-screen bg-indigo-50">
      
      <div className={`fixed inset-y-0 left-0 z-50 w-64 bg-indigo-50 drop-shadow-xl transition duration-300 ease-in-out transform ${menuOpen ? 'translate-x-0' : '-translate-x-full'}`}>
        <div className="flex items-center justify-between p-4">
          <a href="/home">
            <img src="/logo.png" alt="Your Company Logo" className="w-15 h-8 hover:-translate-y--1 hover:scale-110" />
          </a>
           <button className="text-neutral-800 focus:outline-none " onClick={toggleMenu}>
             <MenuOutlined />
          </button>
        </div>
        <nav className="p-4">
          <ul className="space-y-4">
            <li><Link to="/identify" className=" text-white bg-rounded-lg bg-teal-950 flex items-center px-6 py-2 font-medium font-sans"><AreaChartOutlined /> Market Predictions</Link></li>
            <li><Link to="/worldwide-news" className=" text-neutral-800 flex items-center indent-4 hover:font-medium px-3 py-2"><GlobalOutlined /> Worldwide News</Link></li>
            
            <li><Link to="/find-buyers" className=" text-neutral-800 flex items-center indent-4 px-3 py-2 hover:font-medium" ><UserOutlined /> Find Buyers</Link></li>
            <li><Link to="/lessions" className=" text-neutral-800 flex items-center indent-4 px-3 p3-2 hover:font-medium"><BookOutlined /> Find Lessons</Link></li>
            <li><Link to="/escrow" className=" text-neutral-800 flex items-center indent-4 px-3 py-2 hover:font-medium"><DollarCircleOutlined /> Go to Escrow</Link></li>
            <li><Link to="/home" className=" text-amber-500 flex items-center indent-4 px-3 py-2 hover:font-medium" ><RocketOutlined /> C2W Premium</Link></li>
          </ul>
        </nav>
      </div>

      <header className="bg-teal-950 shadow">
        <div class="justify-center pt-1">
            <h2 class="text-amber-500 text-xl font-bold place-self-center mt-2">Market Predictions</h2>
        </div>


        <div className="flex justify-between  px-4 py-2 border-b">
          <button className="text-white focus:outline-none pr-2 hover:text-amber-500"  onClick={toggleMenu}>
            <MenuOutlined />
          </button>
          <div className="flex items-center">
            <div className="text-sm font-medium text-white mr-4">Hello! {userName}</div>
            <button onClick={handleLogout} className="flex items-center text-white hover:text-amber-500">
              <LogoutOutlined className="mr-2" /> Logout
            </button>
          </div>
        </div>
      </header>     
      <div className="flex-grow container mx-auto px-4 py-2 bg-indigo-50 ">
      <div class="overflow-hidden rounded-lg bg-white shadow w-4/5 mr-auto ml-auto mt-8 mb-8">
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4 mt-4">
          <div className="col-span-1 col-start-2 ">
            <select id="country" value={country} onChange={handleCountryChange}
              className="mt-1 block w-full pl-3 pr-10 py-2 text-base border-gray-300 focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm rounded-md shadow-sm">
              <option value="">Select Country</option>
              <option value="France">France</option>
              <option value="Germany">Germany</option>
              <option value="Belgium">Belgium</option>
              <option value="United Kingdom">United Kingdom</option>
              <option value="Spain">Spain</option>
              <option value="Hungary">Hungary</option>
              <option value="Switzerland">Switzerland</option>
              {/* More countries */}
            </select>
          </div>
          <div className="col-span-1">
            <select id="product" value={productID} onChange={handleProductChange}
              className="mt-1 block w-full pl-3 pr-10 py-2 text-base border-gray-300 focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm rounded-md shadow-sm">
              <option value="">Select Product</option>
              <option value="080111">Desiccated coconuts</option>
              <option value="080112">Cinamonan</option>
              {/* Add other product options here */}
            </select>
          </div>
        </div>
        {chartData && Object.keys(chartData).length > 0 ? (
          <div className="mt-4 px-4 py-2 ">
            <Line data={chartData} options={options} />
          </div>
        ) : (
          <div className="mt-4 text-center">No data available</div>
        )}
      </div>

      
      </div>
      <div className="bg-indigo-50">
      <footer class="bg-indigo-50 rounded-lg shadow bg-teal-950 m-4">
        <div class="w-full max-w-screen-xl mx-auto p-4 md:py-8 bg-teal-950 rounded-lg">
          <div class="sm:flex sm:items-center sm:justify-between">
              <a href="/home" class="flex items-center mb-4 sm:mb-0 space-x-3 rtl:space-x-reverse bg-white ml-5 py-3 px-3 pr-2 rounded-lg bg-opacity-85">
                <img src="/logo.png" class="h-8" alt="Flowbite Logo" />
                <span class="self-center text-2xl font-semibold whitespace-nowrap dark:text-white"></span>
              </a> 
            <ul class="flex flex-wrap items-center mb-6 text-sm font-medium text-white-500 sm:mb-0 dark:text-gray-400">
                <li>
                    <a href="#" class="hover:underline me-4 md:me-6">Predict Markets</a>
                </li>
                <li>
                    <a href="#" class="hover:underline me-4 md:me-6">Find Global News</a>
                </li>
                <li>
                    <a href="#" class="hover:underline me-4 md:me-6">Find Buyers</a>
                </li>
                <li>
                    <a href="#" class="hover:underline me-4 md:me-6">Privacy Policy</a>
                </li>
                <li>
                    <a href="#" class="hover:underline">Contact</a>
                </li>
            </ul>
          </div>
          <hr class="my-6 border-gray-200 sm:mx-auto dark:border-gray-700 lg:my-8" />
          <span class="block text-sm text-gray-500 sm:text-center dark:text-gray-400">© 2024 <a href="/home" class="hover:underline">C2W™</a>. All Rights Reserved.</span>
        </div>
        </footer>
        </div>
    </div>
  );
};

export default StockData;
